import {
  ClientSideRowModelModule,
  ColDef,
  ColGroupDef,
  GridApi,
  GridOptions,
  ModuleRegistry,
  NumberEditorModule,
  NumberFilterModule,
  TextEditorModule,
  TextFilterModule,
  TooltipModule,
  ValidationModule,
  createGrid,
} from "ag-grid-community";
import { CustomHeader } from "./customHeader";
import { IOlympicData } from "./interfaces";

ModuleRegistry.registerModules([
  NumberEditorModule,
  TextEditorModule,
  TextFilterModule,
  NumberFilterModule,
  ClientSideRowModelModule,
  TooltipModule,
  ValidationModule /* Development Only */,
]);

const columnDefs: ColDef[] = [
  {
    field: "athlete",
    headerName: "Athlete's Full Name",
    suppressHeaderFilterButton: true,
    minWidth: 120,
  },
  {
    field: "age",
    headerName: "Athlete's Age",
    sortable: false,
    headerComponentParams: { menuIcon: "fa-external-link-alt" },
  },
  {
    field: "country",
    headerName: "Athlete's Country",
    suppressHeaderFilterButton: true,
    minWidth: 120,
  },
  { field: "year", headerName: "Event Year", sortable: false },
  { field: "date", headerName: "Event Date", suppressHeaderFilterButton: true },
  { field: "sport", sortable: false },
  {
    field: "gold",
    headerName: "Gold Medals",
    headerComponentParams: { menuIcon: "fa-cog" },
    minWidth: 120,
  },
  { field: "silver", headerName: "Silver Medals", sortable: false },
  {
    field: "bronze",
    headerName: "Bronze Medals",
    suppressHeaderFilterButton: true,
    minWidth: 120,
  },
  { field: "total", headerName: "Total Medals", sortable: false },
];

let gridApi: GridApi<IOlympicData>;

const gridOptions: GridOptions<IOlympicData> = {
  columnDefs: columnDefs,
  defaultColDef: {
    editable: true,
    filter: true,
    width: 120,
    headerComponent: CustomHeader,
    headerComponentParams: {
      menuIcon: "fa-bars",
    },
  },
};

// setup the grid after the page has finished loading
const gridDiv = document.querySelector<HTMLElement>("#myGrid")!;
gridApi = createGrid(gridDiv, gridOptions);

fetch("https://www.ag-grid.com/example-assets/olympic-winners.json")
  .then((response) => response.json())
  .then((data) => {
    gridApi!.setGridOption("rowData", data);
  });

