    var isWebkit = !!window.navigator.userAgent.match(/AppleWebKit\/([^ ;]*)/);
    var webkitLoadCheck = function (link, callback) {
        setTimeout(function () {
            for (var i = 0; i < document.styleSheets.length; i++) {
