function filter(arrayLike, func) {
    var arr = [];
    forEach(arrayLike, function (item) {
        if (func(item)) arr.push(item);
    });
    return arr;
}

// Because IE8?
function forEach(arrayLike, func) {
    for (var i = 0; i < arrayLike.length; i++) {
        func(arrayLike[i]);
    }
