    'PCLinuxOS',
    'Mageia',
    'Endless',
];

const CITIES = [
    'Tokyo',
    'Jakarta',
    'Delhi',
    'Manila',
    'Seoul',
    'Shanghai',
    'Mumbai',
    'New York',
    'Beijing',
    'Sao Paulo',
