        return new Promise((outerResolve, outerReject) => {
            setTimeout(
                () => {
                    new Promise(function (resolve, reject) {
                        var timeout = setTimeout(function () {
                            reject('Unable to load CSS');
                        }, waitSeconds * 1000);
                        var _callback = function (error) {
                            clearTimeout(timeout);
                            link.onload = link.onerror = noop;
                            setTimeout(function () {
                                if (error) {
                                    reject(error);
                                    outerReject(error);
                                } else {
                                    resolve('');
                                    outerResolve('');
                                }
                            }, 7);
                        };
                        var link = document.createElement('link');
                        link.type = 'text/css';
                        link.rel = 'stylesheet';
                        link.href = url;
                        link.setAttribute('data-systemjs-css', '');
                        if (!isWebkit) {
                            link.onload = function () {
                                _callback();
                            };
                        } else {
                            webkitLoadCheck(link, _callback);
                        }
                        link.onerror = function (event) {
                            _callback(event.error || new Error('Error loading CSS file.'));
                        };
                        if (existingLinks.length) head.insertBefore(link, existingLinks[0]);
                        else head.appendChild(link);
                    })
                        // Remove the old link regardless of loading outcome
                        .then(
                            function (result) {
                                forEach(existingLinks, function (link) {
                                    link.parentElement.removeChild(link);
                                });
                                return result;
                            },
                            function (err) {
                                forEach(existingLinks, function (link) {
                                    link.parentElement.removeChild(link);
                                });
                                throw err;
                            }
