.custom-tooltip {
    position: absolute;
    padding: 20px;
    width: 200px;
    pointer-events: none;
    transition: opacity 1s;
    overflow: auto;
    color: var(--ag-foreground-color);
    background-color: #5577cc;
}

.custom-tooltip.ag-tooltip-hiding {
    opacity: 0;
}

files.customTooltip.js:
class CustomTooltip  {
    eGui;
    init(params) {
        const eGui = (this.eGui = document.createElement('div'));

        eGui.classList.add('custom-tooltip');

        if (params.location === 'setFilterValue') {
            eGui.innerHTML = '<strong>Full value:</strong> ' + params.value;
        } else {
            eGui.innerHTML = params.value;
        }
    }

    getGui() {
        return this.eGui;
    }
}

files.data.js:
function getData() {
    const latinWords =
        'lorem ipsum dolor sit amet consectetur adipiscing elit sed do eiusmod tempor incididunt ut labore'.split(' ');

    function randomSentence() {
        const startIndex = Math.floor(Math.random() * (latinWords.length / 2));
        const str = latinWords.slice(startIndex, latinWords.length).join(' ');
        return str.charAt(0).toUpperCase() + str.slice(1, str.length);
    }

    const rowData = [];
    for (let i = 0; i < 100; i++) {
        const row = {
            colA: randomSentence(),
            colB: randomSentence(),
            colC: randomSentence(),
        };

        rowData.push(row);
    }

    return rowData;
}

