files.customMedalCellRenderer.js:
function CustomMedalCellRenderer(params) {
    const priceSpan = document.createElement('span');
    priceSpan.setAttribute('class', 'imgSpan');
    for (let i = 0; i < params.value ?? 0; i++) {
        const priceElement = document.createElement('img');
        priceElement.src = `https://www.ag-grid.com/example-assets/gold-star.png`;
        priceElement.setAttribute('class', 'medalIcon');
        priceSpan.appendChild(priceElement);
    }
    return priceSpan;
}

files.main.js:
let gridApi;

const gridOptions = {
  columnDefs: [
    { field: "total", rowGroup: true },
    { field: "country" },
    { field: "year" },
    { field: "athlete" },
    { field: "sport" },
  ],
  defaultColDef: {
    flex: 1,
    minWidth: 100,
  },
  autoGroupColumnDef: {
    headerName: "Gold Medals",
    minWidth: 220,
    cellRendererParams: {
      suppressCount: true,
      innerRenderer: CustomMedalCellRenderer,
    },
  },
  groupDisplayType: "multipleColumns",
};

// setup the grid after the page has finished loading
document.addEventListener("DOMContentLoaded", function () {
  var gridDiv = document.querySelector("#myGrid");
  gridApi = agGrid.createGrid(gridDiv, gridOptions);

  fetch("https://www.ag-grid.com/example-assets/olympic-winners.json")
    .then((response) => response.json())
    .then((data) => gridApi.setGridOption("rowData", data));
});
