files.customLoadingOverlay.js:
class CustomLoadingOverlay  {
    eGui;

    init(params) {
        this.eGui = document.createElement('div');
        this.refresh(params);
    }

    getGui() {
        return this.eGui;
    }

    refresh(params) {
        this.eGui.innerHTML = `<div class="ag-overlay-loading-center" role="presentation">
        <div role="presentation" style="height:100px; width:100px; background: url(https://www.ag-grid.com/images/ag-grid-loading-spinner.svg) center / contain no-repeat; margin: 0 auto;"></div>
        <div aria-live="polite" aria-atomic="true">${params.loadingMessage}</div>
     </div>`;
    }
}

files.main.js:
const columnDefs = [
  { field: "athlete", width: 150 },
  { field: "country", width: 120 },
];

const rowData = [
  { athlete: "Michael Phelps", country: "United States" },
  { athlete: "Natalie Coughlin", country: "United States" },
  { athlete: "Aleksey Nemov", country: "Russia" },
  { athlete: "Alicia Coutts", country: "Australia" },
];

let gridApi;

const gridOptions = {
  defaultColDef: {
    editable: true,
    flex: 1,
    minWidth: 100,
    filter: true,
  },

  loading: true,

  columnDefs: columnDefs,
  rowData,

  loadingOverlayComponent: CustomLoadingOverlay,
  loadingOverlayComponentParams: {
    loadingMessage: "One moment please...",
  },
};

function setLoading(value) {
  gridApi.setGridOption("loading", value);
}

// setup the grid after the page has finished loading
document.addEventListener("DOMContentLoaded", () => {
  const gridDiv = document.querySelector("#myGrid");
  gridApi = agGrid.createGrid(gridDiv, gridOptions);
});
